import React, { Component } from 'react';
import ModalImage from "react-modal-image";
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

export default class MovieBlock2 extends Component {
    constructor(props) {
        super(props);
        this.state = { 
          data: [],
          isOpen: false,
        };
      }
    
    //   componentDidMount() {
    //     fetch("http://www.omdbapi.com/?apikey=3f1dca30&i=" + this.props.movieID)
    //       .then(res => res.json())
    //       .then(json => this.setState({ data: json }));
    //   }
    

    render() {
       const isOpen = this.state.isOpen;
        return (
            <div>
              <img className = "movieBlock" src = {this.props.json.Poster} onClick = {() => this.setState({isOpen: true})}></img>
            {/* <ModalImage className = "movieBlock" small = {this.props.json.Poster} large = {this.props.json.Poster} />
            <p> {this.props.json.Title} </p>
            <p> Directed By: {this.props.json.Director}; IMDB Rating: {this.props.json.imdbRating} </p> */}
            {isOpen && (
              <Lightbox mainSrc = {this.props.json.Poster}
               imageTitle = {this.props.json.Title} 
               imageCaption = {"Directed by: " + this.props.json.Director + '   ' + "IMDB Rating: " + this.props.json.imdbRating} 
               onCloseRequest={() => this.setState({ isOpen: false })}
    
              />
            )}
          </div>
        );
    }
}
