import React from 'react';



export default class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      desc: '',
      message: '',

    };
  }
  myChangeHandler = (event) => {
    let nam = event.target.name;
    let val = event.target.value;
    this.setState({[nam]: val});
  }

  submitForm = (event) => {

  }

  render() {
    return (
    <>
      <form>
      <h1> Log Your Visit! {this.state.username} {this.state.age}</h1>
      <p> Name: </p>
      <input
        type='text'
        name='name'
        onChange={this.myChangeHandler}
      />
      <p> Description: </p>
      <input
        type='text'
        name='desc'
        onChange={this.myChangeHandler}
      />
      <p> Message: </p>
      <input
        type='text'
        name='desc'
        onChange={this.myChangeHandler}
      />
      <p>Make message visible to other guests?</p>
      <input
        type='checkbox'
        name='visible'
        onChange={this.myChangeHandler}
      /> 
      <p> Message: </p>
      <input
        type='text'
        name='message'
        onChange={this.myChangeHandler}
      />
      <p> Email (Optional): </p>
      <input
        type='text'
        name='email'
        onChange={this.myChangeHandler}
      />
      </form>
      <button type = "submit" form = "form1" onClick = {this.submitForm}> Submit </button>
    </>
    );
  }
}
