                // time: data[item].time,
                data[item]
              );
            }
            this.setState({
              display: newState,
            });
          });
    }

    render() {
        return(
        <div className = "movieBlockDiv">
            <MovieOptions />

            {this.state.movies.map((movieJSON, index) => (
                <MovieBlock key={index} json={movieJSON} />
            ))}
        </div>
        )
    }

}
