import React, { Component } from 'react';
import MovieBlock from "./MovieBlock";
import MovieOptions from "./MovieOptions";

import config from "../config";
import "./FeedbackPage.css"
const firebase = require("firebase");

// API Key:
//http://www.omdbapi.com/?apikey=3f1dca30&i=tt3896198

export default class MoviePage extends Component {
    constructor() {
        super();
        this.state = {
            movies: []
        }
        this.addMovie = (movie) => {
            var movieList = this.state.movies.concat(movie);
            this.setState({
                movies: movieList
            })
        }
    }

    componentDidMount() {
        if (!firebase.apps.length) {
            firebase.initializeApp(config)
         } 
        let ref = firebase.database().ref("movieid");
          ref.on("value", (snapshot) => {
            let newState = [];
            const data = snapshot.val();
            for (let item in data) {
              newState.push(
                data[item]
              );
            }
            this.setState({
              movies: newState,
            });
          });
    }

    render() {
        return(
        <div className = "movieBlockDiv">
            <MovieOptions />

            {this.state.movies.map((movieJSON, index) => (
                <MovieBlock key={index} json={movieJSON} />
            ))}
        </div>
        )
    }

}
