import React, { Component } from 'react'
import axios from 'axios';
import config from "../config";

const firebase = require("firebase");



export default class MovieOptions extends Component {
    
    constructor() {
        super();
        this.state = {
          movieid: "",
          time: null,
          movieRef: null,
          list: ""
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleNewList = this.handleNewList.bind(this);
      }
    
      handleChange(e) {
        this.setState({
          [e.target.name]: e.target.value,
        });
      }

     // tt1596363
      handleValidation() {
        if (this.state.movieid === "") {
          alert("ID cannot be empty.");
        } else if (this.state.movieid.length != 9) {
          alert("ID should be 9 characters.");
         } else return true;
        return false;
      }

      handleNewList(e) {
        e.preventDefault();
        const movieRef = firebase.database().ref("lists").push(this.list)
            .then(() => this.setState({list: ""})
        );
        alert("New list submitted");
      }
    

    //   var data = require('./data/movies.json');
    //   data.movies.map((movieID, index) => (
    //       axios.get('https://www.omdbapi.com/?apikey=fe15e914&i=' + movieID)
    //           .then(response => {
    //               this.addMovie(response.data);
    //           })
    //   ));

    //Poster, Title, Director, imdbRating


      handleSubmit(e) {
        e.preventDefault();
        if (this.handleValidation()) {
          axios.get('https://www.omdbapi.com/?apikey=fe15e914&i=' + this.state.movieid)
            .then(response => {
                const movieRef = firebase.database().ref("movieid/" + this.state.movieid);
                movieRef.set(response.data);
            }).then(() => {
                this.setState({
                    movieid: "",
                    time: null,
                  });
            })
          alert("New movie submitted");
        }
      }

      componentDidMount() {
        if (!firebase.apps.length) {
            firebase.initializeApp(config)
         } 
      }
    

    render() {
        return (
            <div>
                <h1 style = {{textAlign: "center"}}> Add Movie </h1>
                <form className = "movieAddForm" onSubmit = {this.handleSubmit}>
                    <p style = {{width: "50%", margin: "auto"}}>
                    {" "}
                    <input
                        type="text" 
                        name="movieid"
                        onChange={this.handleChange}
                        value={this.state.movieid}
                    />
                    </p>
                </form>
                <h1> Make new list </h1>
                <form className = "listAddForm" onSubmit = {this.handleNewList}>
                    <input type = "text" name = "list"
                        onChange= {this.handleChange}
                        value = {this.state.list}
                    />
                </form>
            </div>
        )
    }
}
