import React, { Component } from "react";
import config from "../config";

const firebase = require("firebase");

class Form extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      desc: "",
      message: "",
      visible: null,
      email: "",
      time: null,
      display: [],
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  myChangeHandler(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handleValidation() {
    if (this.state.name === "") {
      alert("Name cannot be empty.");
    } else if (this.state.name.length < 5) {
      alert("Your name must be longer than 5 letters.");
    } else if (this.state.name.length > 19) {
      alert("Your name must be shorter than 20 letters.");
    } else if (this.state.message.length < 15) {
      alert("Your message must be longer than 15 characters.");
    } else if (this.state.message.length > 500) {
      alert("Your message must be shorter than 500 characters.");
    } else if (this.state.private === null) {
      alert("Choose whether message will be private or public.");
    } else return true;
    return false;
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.handleValidation()) {
      const itemsRef = firebase.database().ref("messages");
      const item = {
        name: this.state.name,
        desc: this.state.description,
        message: this.state.message,
        private: this.state.private,
        email: this.state.email,
        time: Date(firebase.database.ServerValue.TIMESTAMP),
      };
      itemsRef.push(item);
      this.setState({
        name: "",
        desc: "",
        message: "",
        email: "",
        time: null,
      });
      alert("Form submitted");
    }
  }

  componentDidMount() {
    firebase.initializeApp(config);
    let ref = firebase.database().ref("messages");
    ref.on("value", (snapshot) => {
      let newState = [];
      const data = snapshot.val();
      for (let item in data) {
        newState.push({
          id: item,
          name: data[item].username,
          description: data[item].desc,
          message: data[item].message,
          private: data[item].visible,
          email: data[item].email,
          time: data[item].time,
        });
      }
      this.setState({
        display: newState,
      });
    });
  }

  componentDidUpdate(prevProps, prevState, snapshot) {
    //only call set state here if it is wrapped in a condition
    //if you initialize this.state.shouldUpdate and have not changed it yet then this will not run
    if (this.state.shouldUpdate !== prevState.shouldUpdate) {
      //same code as above to retrieve the data
    }
  }

  render() {
    return (
      <>
        <div className="mainForm">
          <form id="myForm" onSubmit = {this.handleSubmit}>
            <h1>
              {" "}
              Log Your Visit! {this.state.username} {this.state.age}
            </h1>
            <p>
              {" "}
              Name:
              <input
                type="text"
                name="name"
                onChange={this.myChangeHandler}
                value={this.state.name}
              />{" "}
            </p>

            <p>
              {" "}
              Description:
              <input
                type="text"
                name="desc"
                onChange={this.myChangeHandler}
                value={this.state.desc}
              />{" "}
            </p>

            <p>
              {" "}
              Message:
              <input
                type="text"
                name="message"
                onChange={this.myChangeHandler}
                value={this.state.message}
              />{" "}
            </p>

            <p>
              Make message visible to other guests?
              <input
                type="checkbox"
                name="visible"
                onChange={this.myChangeHandler}
                value={this.state.visible}
              />{" "}
            </p>

            <p>
              {" "}
              Email (Optional):
              <input
                type="text"
                name="email"
                onChange={this.myChangeHandler}
                value={this.state.name}
              />{" "}
            </p>
            <button type="submit" form="form1">
            {" "}
            Submit{" "}
          </button>
          </form>
        </div>
        <div className="displayResponses">
          <ul className="entry">
            {this.state.display.map((item) => {
              if (item.private === "false") {
                return (
                  <li key={item.id}>
                    <p>{item.time}</p>
                    <h3>{item.name}</h3>
                    <p>{item.desc}</p>
                    <p>Message: {item.message}</p>
                  </li>
                );
              }
            })}
          </ul>
        </div>
      </>

      // <div className='app'>
      // 	<div className='container'>
      //       	<div className='add-item'>
      //       		<h2 align='center'> Enter Messages Here</h2>
      //           		<form onSubmit={this.handleSubmit}>
      // 	                <input type="text" name="name" placeholder="What's your name?" onChange={this.handleChange} value={this.state.name}/>
      // 	                <input type="text" name="description" placeholder="Give a short description of yourself." onChange={this.handleChange} value={this.state.description}/>
      // 	                <input type="text" name="message" placeholder="What do you have to say?" onChange={this.handleChange} value={this.state.message}/>
      // 	                <p>Would you like your message to be private?</p>
      // 	                <input type="radio" id="yes" name="private" onChange={this.handleChange} value={true}/>
      // 	                <label for="yes">Yes</label>
      // 					<input type="radio" id="no" name="private" onChange={this.handleChange} value={false}/>
      // 					<label for="no">No</label>
      // 	                <input type="email" name="email" placeholder="If you want to be contacted, what's your email?" onChange={this.handleChange} value={this.state.email} />
      // 	                <button>Send Message</button>
      //           		</form>
      //       	</div>
      //   	<div className='display-item'>
      //     	<div className='wrapper'>
      //       		<ul className='entry'>
      //           		{this.state.display.map((item) => {
      //           			if (item.private === "false") {
      // 			        return (
      // 			          <li key={item.id}>
      // 			          	<p>{item.time}</p>
      // 			            <h3>{item.name}</h3>
      // 				        <p>{item.description}</p>
      // 			            <p>Message: {item.message}</p>
      // 			          </li>
      // 			        )
      // 			      }})}
      //       		</ul>
      //     	</div>
      //   	</div>
      // 	</div>
      // </div>
    );
  }
}
export default Form;
