import React, { Component } from 'react'
import movies from './data/movies.js'
import ModalImage from "react-modal-image";


// API Key:
//http://www.omdbapi.com/?apikey=3f1dca30&i=tt3896198


// fetch(url).then(function(response) {
//     return response.json();
//   }).then(function(data) {
//     console.log(data);
//   }).catch(function() {
//     console.log("Booo");
// });

// poster, title, director, imdb rating
let movie_list = []
let url_list = []
let retrieved_list = []

export default class MoviePage extends Component {
    componentDidMount() {
        movie_list = movies.split(", ");
        for (var i = 0; i < movie_list.length; i++) {
            url_list.push("http://www.omdbapi.com/?apikey=3f1dca30&i=" + movie_list[i]);
            fetch(url_list[i])
                .then(response => response.json())
                .then(json => retrieved_list.push(json));
        }
        const movieBlock = retrieved_list.map((moviejson) =>
        <div>
            <ModalImage className = "ModalImage" small = {moviejson["Poster"]} large = {moviejson["Poster"]}> hi </ModalImage>
            <p> {moviejson["Title"]} </p>
            <p> Directed by: {moviejson["Director"]} Rating: {moviesjson["imdbRating"]}</p>
        </div>
    );
    }

    // const movieBlock = retrieved_list.map((moviejson) =>
    //     <div>
    //         <ModalImage className = "ModalImage" small = {moviejson["Poster"]} large = {moviejson["Poster"]}> hi </ModalImage>
    //         <p> {moviejson["Title"]} </p>
    //         <p> Directed by: {moviejson["Director"]} Rating: {moviesjson["imdbRating"]}</p>
    //     </div>
    // );



    render() {
        return (
            <div>
            <br/><br/>
            <h1> Movies </h1>

            {movieBlock}
            
          </div>
        )
    }
}
