import React, { Component } from 'react';
import movies from './data/movies.js';
import ModalImage from "react-modal-image";
import MovieBlock2 from "./MovieBlock2";

// API Key:
//http://www.omdbapi.com/?apikey=3f1dca30&i=tt3896198

export default class MoviePage extends Component {
    constructor() {
        super();
        this.state = {
            movies: []
        }
        this.addMovie = (movie) => {
            var movieList = this.state.movies.concat(movie);
            this.setState({
                movies: movieList
            })
        }
    }

    componentDidMount() {
        var data = require('./movies.json');
        data.movies.map((movieID, index) => (
            fetch('https://www.omdbapi.com/?apikey=fe15e914&i=' + movieID)
                .then(response => {
                    this.addMovie(response.data);
                })
        ));
    }

    render() {
        return(
        <div>
            {this.state.movies.map((movieJSON, index) => (
                <MovieBlock2 key={index} json={movieJSON} src={movieJSON.Poster} />
            ))}
        </div>
        )
    }

}
